document.addEventListener("DOMContentLoaded",function(){let t=document.querySelectorAll(".faq_toggle");t.forEach(t=>{t.addEventListener("click",function(){this.classList.contains("open")||document.querySelectorAll(".faq_toggle.open").forEach(t=>{t.click()});let t=this.nextElementSibling;if(this.classList.contains("open"))t.style.transition="height 400ms",t.style.height="0px";else{t.style.height="auto";let e=t.offsetHeight;t.style.height="0px",setTimeout(()=>{t.style.transition="height 400ms",t.style.height=`${e}px`},10),t.addEventListener("transitionend",function(){t.style.height="auto"},{once:!0})}this.classList.toggle("open")})})});
